import React, { useState, useContext, useRef } from "react";
import classes from "./Board.module.scss";
import logoDark from "../../assets/logo-no-background.svg";
import IconVerticalEllipses from "../Icons/IconVerticalEllipses";
import AddNewTask from "../AddNewTask/AddNewTask";
import EditBoard from "../EditBoard/EditBoard";
import Column from "../Column/Column";
import { ModalContext } from "../../store/modal-context";
import { TranslateContext } from "../../store/boardTranslate-context";

const Board = () => {
  // Contexts
  const { translateX, boards, activeBoardIndex, createNewBoard } = useContext(TranslateContext);

  // Local State
  const [columns, setColumns] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Functions
  const openModal = () => setModalIsOpen(true);

  const addNewColumn = () => {
    const newColumn = { id: Date.now(), name: `Column ${columns.length + 1}`, tasks: [] };
    setColumns((prevColumns) => [...prevColumns, newColumn]);
  };

  // Add task functionality - will be called from the AddNewTask component
  const addTaskToColumn = (columnId, task) => {
    setColumns((prevColumns) =>
      prevColumns.map((column) =>
        column.id === columnId
          ? { ...column, tasks: [...column.tasks, task] }
          : column
      )
    );
  };

  return (
    <ModalContext.Provider value="none">
      {modalIsOpen && <AddNewTask />}
      <section className={classes.board_} style={{ transform: `translateX(${translateX})` }}>
        <header className={classes.header_}>
          <div className={classes.logo}>
            <img src={logoDark} alt="logo_light" />
          </div>
          <div className={classes.header_text}>
            <span>{boards[activeBoardIndex]?.title}</span>
            <div className={classes.header_text_right}>
              <button className={classes.add_new_tasks} onClick={openModal}>
                + Add New Task
              </button>
              <IconVerticalEllipses />
            </div>
          </div>
        </header>

        <section className={classes.kanban_app_body}>
          <section className={classes.kanban_body_content}>
            {columns.length ? (
              columns.map((column) => (
                <Column key={column.id} column={column} addTask={(task) => addTaskToColumn(column.id, task)} />
              ))
            ) : (
              <p>No columns yet. Click below to add one!</p>
            )}
            <div className={classes.create_new_column}>
              <button onClick={addNewColumn}>+ Add New Column</button>
            </div>
          </section>
        </section>
      </section>
    </ModalContext.Provider>
  );
};

export default Board;
