import { TaskContext } from "../../store/newTaskContext";
import { useContext } from "react";
const AddNewTask = () => {
  const { modalIsHidden } = useContext(TaskContext);
  const modal = !modalIsHidden ? (
    <Modal>
      <section className={classes.add_new_task}>NewTask</section>;
      <button>add new task</button>
    </Modal>
  ) : null;
  return (
   {modal}
  );
};

export default AddNewTask;
