import React, { useContext, useState } from "react";
import classes from "./AddNewTask.module.scss";
import Modal from "../Modal/Modal";
import { TaskContext } from "../../store/newTaskContext";
import { TaskProvider } from "../../store/newTaskContext";
const AddNewTask = ({ closeModal }) => {
  const { addNewTask, columns } = useContext(TaskContext);
  const [taskTitle, setTaskTitle] = useState("");
  const [selectedColumnId, setSelectedColumnId] = useState(columns[0]?.id || "");

  const handleAddTask = () => {
    if (taskTitle && selectedColumnId) {
      addNewTask(selectedColumnId, taskTitle);
      setTaskTitle("");
      closeModal(); // Close modal after adding task
    }
  };

  return (
    <Modal>
      <section className={classes.add_new_task}>
        <h3>New Task</h3>
        <input
          type="text"
          value={taskTitle}
          onChange={(e) => setTaskTitle(e.target.value)}
          placeholder="Task title"
        />
        <select
          value={selectedColumnId}
          onChange={(e) => setSelectedColumnId(Number(e.target.value))}
        >
          {columns.map((column) => (
            <option key={column.id} value={column.id}>
              {column.name}
            </option>
          ))}
        </select>
        <></>
        <button onClick={handleAddTask}>Add Task</button>
      </section>
    </Modal>
  );
};

export default AddNewTask;
