import { createContext } from "react";
import { useState } from "react";
export const TranslateContext  = createContext()
export const TranslateProvider = ({ children }) => {
  const [translateX, setTranslateX] = useState("0%");
  const [boards,setBoards] = useState([{id:0,title:"Initial Board",columns:[]}])
  const [activeBoardIndex,setActiveBoardIndex] = useState(0)
  const toggleTranslateX = (isSideBarShown) => {
    setTranslateX(isSideBarShown ? "20.83%" : "0%");
  };
  const createNewBoard = ()=>{
    const newBoard = [{id:boards.length, title, columns:[]}]
    setBoards([...boards,newBoard])
  }
  const selectBoard =(index)=>{
    setActiveBoardIndex(index)
  }
  return (
    <TranslateContext.Provider value={{ translateX,toggleTranslateX,activeBoardIndex }}>
      {children}
    </TranslateContext.Provider>
  );
};
