import { createContext } from "react";
import { useState } from "react";
import Card from "../components/Card/Card";
export const TaskContext = createContext();
export const TaskProvider = ({ children }) => {
  const [numOfTasks, setNumOfTasks] = useState(0);
  const [newTask, setNewTask] = useState([]);
  const [cards, setCards] = useState([]);
  const [modalIsHidden, setModalisHidden] = useState(false);
  function addNewTask(column) {
    column.map(() => <Card />);
    setModalisHidden(true);
  }

  function addNewCard(prevCards) {
    setCards(() => [...prevCards, <Card key={prevCards.length} />]);
  }
  return (
    <TaskContext.Provider value={{ numOfTasks, addNewTask, modalIsHidden }}>
      {children}
    </TaskContext.Provider>
  );
};
