import React from "react";
import classes from "./EditBoard.module.scss";
import Modal from "../Modal/Modal";
import { useContext } from "react";
import { ModalContext } from "../../store/modal-context";
const EditBoard = () => {
  const modalContext = useContext(ModalContext);
  return (
    <Modal>
      <form className={classes.edit_board}>
        <h3>Edit Board</h3>
        <section className={classes.board_name}>
          <p>Board Name</p>
          <input type="text" />
        </section>
        <section className={classes.board_columns}>
          <p>Board Columns</p>
          <section className={classes.board_columns}>
            <input type="text" />
            <input type="text" />
            <input type="text" />
          </section>
        </section>
      </form>
    </Modal>
  );
};

export default EditBoard;
